import React, { Component } from "react";

// 할일을 등록할 수 있는 form

class TodoInsertForm extends Component {
  render() {
    const {
      wordonChange,
      word,
      meaning,
      insertHandler,
      meaningchangeHandler,
    } = this.props;
    return (
      <div className="insert_border">
        <div>
          word
          <input
            type="text"
            onChange={wordonChange}
            value={word}
            className="insert_input"
          />
        </div>
        <div>
          meaning
          <input type="text" onChange={meaningchangeHandler} value={meaning} />
        </div>
        <button onClick={insertHandler}>입력</button>
      </div>
    );
  }
}

export default TodoInsertForm;
