import React, { Component } from "react";
import { InputBorder, InputInsert } from "./TodoInsertForm.styled";

// 할일을 등록할 수 있는 form

class TodoInsertForm extends Component {
  render() {
    const {
      wordonChange,
      word,
      meaning,
      insertHandler,
      meaningchangeHandler,
    } = this.props;
    return (
      <InputBorder>
        <InputInsert className="insert_input">
          word
          <input
            type="text"
            onChange={wordonChange}
            value={word}
            className="insert_input"
          />
        </InputInsert>
        <div>
          meaning
          <input type="text" onChange={meaningchangeHandler} value={meaning} />
        </div>
        <button onClick={insertHandler}>입력</button>
      </InputBorder>
    );
  }
}

export default TodoInsertForm;
