import styled from "styled-components";
import palette from "../../utils/styles";

const InputBorder = styled.div`
  border: 1px solid red;
`;

const InputInsert = styled.input`
  input {
    border: none;
    &:focus {
      outline: none;
    }
  }
`;

export { InputBorder, InputInsert };
