import React, { Component } from "react";
import TextareaAutosize from "react-textarea-autosize";
import "./TodoInsertForm.scss";
// 할일을 등록할 수 있는 form

class TodoInsertForm extends Component {
  render() {
    const {
      wordonChange,
      word,
      meaning,
      insertHandler,
      meaningchangeHandler,
    } = this.props;
    return (
      <div className="board_box">
        <div>
          word
          <TextareaAutosize
            type="text"
            onChange={wordonChange}
            value={word}
            className="insert_input"
          />
        </div>
        <div>
          meaning
          <TextareaAutosize
            type="text"
            onChange={meaningchangeHandler}
            value={meaning}
          />
        </div>
        <button onClick={insertHandler}>입력</button>
      </div>
    );
  }
}

export default TodoInsertForm;
